import React from 'react';

const Header = () => {
    return (
        <header>
            <h1>Ecommerce App</h1>
            <nav>
                <ul>
                    <li><a href="#">Home</a></li>
                    <li><a href="#">Products</a></li>
                    <li><a href="#">Cart</a></li>
                </ul>
            </nav>
        </header>
    );
}

export default Header;

import React from 'react';

const Footer = () => {
    return (
        <footer>
            <p>&copy; 2023 Ecommerce App. All rights reserved.</p>
        </footer>
    );
}

export default Footer;

import React from 'react';

const ProductList = () => {
    // Dummy product data
    const products = [
        { id: 1, name: 'Product 1', price: '$10' },
        { id: 2, name: 'Product 2', price: '$20' },
        { id: 3, name: 'Product 3', price: '$30' },
    ];

    return (
        <div>
            <h2>Product List</h2>
            <ul>
                {products.map(product => (
                    <li key={product.id}>
                        {product.name} - {product.price}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default ProductList;

import React from 'react';

const ProductDetail = () => {
    // Dummy product detail data
    const product = { id: 1, name: 'Product 1', price: '$10', description: 'This is a great product!' };

    return (
        <div>
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <p>Price: {product.price}</p>
            <button>Add to Cart</button>
        </div>
    );
}

export default ProductDetail;

import React from 'react';

const Cart = () => {
    // Dummy cart data
    const cartItems = [
        { id: 1, name: 'Product 1', quantity: 2, price: '$10' },
        { id: 2, name: 'Product 2', quantity: 1, price: '$20' },
    ];

    return (
        <div>
            <h2>Shopping Cart</h2>
            <ul>
                {cartItems.map(item => (
                    <li key={item.id}>
                        {item.name} - {item.price} x {item.quantity}
                    </li>
                ))}
            </ul>
            <button>Proceed to Checkout</button>
        </div>
    );
}

export default Cart;

import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import ProductList from './components/ProductList';
import ProductDetail from './components/ProductDetail';
import Cart from './components/Cart';

function App() {
  return (
    <Router>
      <div className="App">
        <Header />
        <Switch>
          <Route path="/" exact component={ProductList} />
          <Route path="/product/:id" component={ProductDetail} />
          <Route path="/cart" component={Cart} />
        </Switch>
        <Footer />
      </div>
    </Router>
  );
}

export default App;

