content.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {

    --scrollbar-thumb: rgba(100, 100, 100, 1);
  	--scrollbar-track: rgba(100, 100, 100, 1);

    --background: 0 0% 100%;
    --foreground: 240 10% 3.9%;
    --card: 0 0% 100%;
    --card-foreground: 240 10% 3.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 240 10% 3.9%;
    --primary: 240 5.9% 10%;
    --primary-foreground: 0 0% 98%;
    --secondary: 240 4.8% 95.9%;
    --secondary-foreground: 240 5.9% 10%;
    --muted: 240 4.8% 95.9%;
    --muted-foreground: 240 3.8% 46.1%;
    --accent: 240 4.8% 95.9%;
    --accent-foreground: 240 5.9% 10%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 0 0% 98%;
    --border: 240 5.9% 90%;
    --input: 240 5.9% 90%;

    --radius: 0.5rem;

  }

  .dark {

    --scrollbar-thumb: rgba(50, 50, 50, 1);
  	--scrollbar-track: rgba(50, 50, 50, 1);

    --background: 240 10% 3.9%;
    --foreground: 0 0% 98%;
    --card: 240 10% 3.9%;
    --card-foreground: 0 0% 98%;
    --popover: 240 10% 3.9%;
    --popover-foreground: 0 0% 98%;
    --primary: 0 0% 98%;
    --primary-foreground: 240 5.9% 10%;
    --secondary: 240 3.7% 15.9%;
    --secondary-foreground: 0 0% 98%;
    --muted: 240 3.7% 15.9%;
    --muted-foreground: 240 5% 64.9%;
    --accent: 240 3.7% 15.9%;
    --accent-foreground: 0 0% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 0 0% 98%;
    --border: 240 3.7% 15.9%;
    --input: 240 3.7% 15.9%;

  }
}

*{
  font-family: 'Inter', sans-serif;
}


*::selection {
	background: #3700b3;
	color: white;
}

::-webkit-scrollbar-thumb {
  background-color: var(--scrollbar-thumb);
  opacity: 0.5; /* Adjust opacity as needed */
}

::-webkit-scrollbar-track {
  background-color: var(--scrollbar-track);
}

::-webkit-scrollbar {
  width: 2px; /* Adjust scrollbar width as needed */
  border-radius: 0px; /* Adjust scrollbar corner radius as needed */
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply bg-background text-foreground;
  }
}
